import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Paper,
  Box,
  CircularProgress,
  Alert,
  Grid,
  Divider,
  CssBaseline,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { CheckCircle } from '@mui/icons-material';
import axios from 'axios';
import { LineChart, Line, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#181a1b',
      paper: '#23272a',
    },
    primary: {
      main: '#2196f3',
    },
    secondary: {
      main: '#f50057',
    },
    text: {
      primary: '#fff',
      secondary: '#b0b3b8',
    },
  },
  typography: {
    fontFamily: 'Inter, Roboto, Arial, sans-serif',
  },
});

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:5001',
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true,
  timeout: 30000 // Increased timeout to 30 seconds
});

function App() {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [history, setHistory] = useState([]);

  // Fetch recent scan history
  useEffect(() => {
    fetch('/api/history')
      .then(res => res.json())
      .then(data => setHistory(data))
      .catch(() => setHistory([]));
  }, [result]); // refetch when a new result is shown

  const analyzeUrl = async () => {
    if (!url) {
      setError('Please enter a URL');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      console.log('Sending request to analyze URL:', url);
      const response = await api.post('/api/analyze', { url });
      console.log('Received response:', response.data);
      setResult(response.data);
    } catch (err) {
      console.error('Error details:', err);
      if (err.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        setError(err.response.data.error || 'Server error occurred');
      } else if (err.request) {
        // The request was made but no response was received
        setError('No response from server. Please check if the backend is running.');
      } else {
        // Something happened in setting up the request that triggered an Error
        setError('Error setting up the request');
      }
    } finally {
      setLoading(false);
    }
  };

  const getRiskColor = (level) => {
    if (!level) return '#2196f3';
    if (level.toLowerCase().includes('high')) return '#ff5252';
    if (level.toLowerCase().includes('medium')) return '#ffb300';
    if (level.toLowerCase().includes('low')) return '#4caf50';
    return '#2196f3';
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h2" component="h1" align="center" gutterBottom sx={{ fontWeight: 700 }}>
          Cyber Threat Detector
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ color: 'text.secondary', mb: 4 }}>
          Real-time URL Threat Analysis
        </Typography>
        <Paper elevation={3} sx={{ p: 3, mb: 3, background: '#23272a', width: '100%' }}>
          <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
            <TextField
              fullWidth
              label="Enter URL to analyze"
              variant="outlined"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="https://example.com"
              sx={{ input: { color: '#fff' } }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={analyzeUrl}
              disabled={loading}
              sx={{ minWidth: 140, fontWeight: 600, fontSize: 18 }}
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : 'ANALYZE'}
            </Button>
          </Box>

          {/* Recent Scans Section */}
          {history.length > 0 && (
            <Box sx={{ mb: 4 }}>
              <Typography variant="h6" sx={{ color: 'text.primary', mb: 1 }}>
                Recent Scans
              </Typography>
              <Paper elevation={1} sx={{ background: '#181a1b', p: 2 }}>
                {history.map((scan) => (
                  <Box key={scan._id} sx={{ mb: 1, cursor: 'pointer' }}
                    onClick={() => setUrl(scan.url)}>
                    <Typography variant="body2" sx={{ color: '#90caf9', textDecoration: 'underline' }}>
                      {scan.url}
                    </Typography>
                    <Typography variant="caption" sx={{ color: 'text.secondary', ml: 1 }}>
                      {scan.result.risk_level_desc} | {new Date(scan.timestamp).toLocaleString()}
                    </Typography>
                  </Box>
                ))}
              </Paper>
            </Box>
          )}

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          {result && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 2 }}>
                Analysis Results
              </Typography>
              <Paper elevation={2} sx={{ p: 3, background: '#181a1b', width: '100%' }}>
                <Typography variant="subtitle1" sx={{ color: getRiskColor(result.risk_level_desc), fontWeight: 700, mb: 1 }}>
                  Risk Level: {result.risk_level_desc}
                </Typography>
                <Typography variant="body2" sx={{ color: getRiskColor(result.risk_level_desc), mb: 2 }}>
                  {result.vendor_message}
                </Typography>
                <Grid container spacing={4}>
                  {/* Left Column: Stats */}
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Traffic Statistics
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Daily Visitors:</b> {result.traffic_stats?.daily_visitors}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Bounce Rate:</b> {result.traffic_stats?.bounce_rate}%
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Average Visit Duration:</b> {result.traffic_stats?.avg_visit_duration} minutes
                      </Typography>
                    </Box>
                    <Divider sx={{ my: 2, background: '#333' }} />
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Real-time Monitoring
                    </Typography>
                    <Box sx={{ mb: 2, background: '#23272a', p: 2, borderRadius: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Current Visitors:</b> {result.real_time_monitoring?.current_visitors}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Peak Visitors:</b> {result.real_time_monitoring?.peak_visitors}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Total Visits:</b> {result.real_time_monitoring?.total_visits}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Trend:</b> {result.real_time_monitoring?.trend}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Last Updated:</b> {result.real_time_monitoring?.last_updated}
                      </Typography>
                      <Typography variant="body2" sx={{ color: '#4caf50', fontWeight: 600 }}>
                        ✔ Real-time monitoring {result.real_time_monitoring?.status?.toLowerCase() === 'active' ? 'active' : 'inactive'}
                      </Typography>
                      <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                        Data Source: Real-time Monitoring
                      </Typography>
                    </Box>
                  </Grid>
                  {/* Right Column: Threat Analysis & Chart */}
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Threat Analysis
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Vendor Count (Malicious):</b> {result.vendor_count}
                      </Typography>
                    </Box>
                    <Divider sx={{ my: 2, background: '#333' }} />
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Security Vendor Analysis
                    </Typography>
                    <Box sx={{ height: 220, background: '#23272a', borderRadius: 2, p: 2 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={result.chart_data || []}>
                          <CartesianGrid stroke="#333" />
                          <XAxis dataKey="name" stroke="#fff" />
                          <YAxis stroke="#fff" allowDecimals={false} />
                          <Tooltip />
                          <Legend />
                          <Area
                            type="monotone"
                            dataKey={d => d.name === 'Safe' ? 'vendors' : undefined}
                            stroke="#4caf50"
                            fill="#4caf50"
                            fillOpacity={0.3}
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            name="Safe"
                            data={result.chart_data.filter(d => d.name === 'Safe')}
                          />
                          <Area
                            type="monotone"
                            dataKey={d => d.name === 'Undetected' ? 'vendors' : undefined}
                            stroke="#90caf9"
                            fill="#90caf9"
                            fillOpacity={0.3}
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            name="Undetected"
                            data={result.chart_data.filter(d => d.name === 'Undetected')}
                          />
                          <Area
                            type="monotone"
                            dataKey={d => d.name === 'Suspicious' ? 'vendors' : undefined}
                            stroke="#ffb300"
                            fill="#ffb300"
                            fillOpacity={0.3}
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            name="Suspicious"
                            data={result.chart_data.filter(d => d.name === 'Suspicious')}
                          />
                          <Area
                            type="monotone"
                            dataKey={d => d.name === 'Malicious' ? 'vendors' : undefined}
                            stroke="#ff5252"
                            fill="#ff5252"
                            fillOpacity={0.3}
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            name="Malicious"
                            data={result.chart_data.filter(d => d.name === 'Malicious')}
                          />
                          <Line
                            type="monotone"
                            dataKey="vendors"
                            stroke="#2196f3"
                            strokeWidth={3}
                            dot={{ r: 6 }}
                            name="Threat Analysis"
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            activeDot={{ r: 10, stroke: "#fff", strokeWidth: 2, fill: "#2196f3" }}
                          />
                        </LineChart>
                      </ResponsiveContainer>
                    </Box>
                    <Typography variant="caption" sx={{ color: 'text.secondary', mt: 1, display: 'block' }}>
                      The chart shows the number of security vendors that have classified this URL as safe, suspicious, or malicious. Hover over the data points for detailed information.
                    </Typography>
                  </Grid>
                </Grid>
                <Typography variant="caption" sx={{ color: 'text.secondary', mt: 2, display: 'block' }}>
                  {result.explanatory_note}
                </Typography>
              </Paper>
            </Box>
          )}
        </Paper>
      </Container>
    </ThemeProvider>
  );
}

export default App;
