import React, { useState } from 'react';
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  Paper,
  Grid,
  CircularProgress,
  CssBaseline,
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
} from 'recharts';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#181a1b',
      paper: '#23272a',
    },
    primary: {
      main: '#2196f3',
    },
    text: {
      primary: '#fff',
      secondary: '#b0b3b8',
    },
  },
  typography: {
    fontFamily: 'Inter, Roboto, Arial, sans-serif',
  },
});

function getRiskColor(level) {
  if (!level) return '#2196f3';
  if (level.toLowerCase().includes('high')) return '#ff5252';
  if (level.toLowerCase().includes('medium')) return '#ffb300';
  if (level.toLowerCase().includes('low')) return '#4caf50';
  return '#2196f3';
}

// Helper to get risk level from threat intelligence results
function getRiskLevel(result) {
  // Check for malicious verdicts in intelligence_results
  const intel = result.intelligence_results || {};
  if (
    (intel.urlhaus && intel.urlhaus.is_malware) ||
    (intel.phishtank && intel.phishtank.is_phishing) ||
    (intel.google_safe_browsing && intel.google_safe_browsing.threats && intel.google_safe_browsing.threats.length > 0) ||
    (intel.ipqualityscore && (intel.ipqualityscore.is_malware || intel.ipqualityscore.is_phishing || intel.ipqualityscore.is_high_risk)) ||
    (intel.threatfox && intel.threatfox.is_malware)
  ) {
    return 'High Risk';
  }
  // Check for suspicious verdicts
  if (
    (intel.ipqualityscore && intel.ipqualityscore.is_suspicious) ||
    (intel.google_safe_browsing && intel.google_safe_browsing.threats && intel.google_safe_browsing.threats.some(t => t.threatType && t.threatType.toLowerCase().includes('suspicious')))
  ) {
    return 'Medium Risk';
  }
  // Otherwise, safe
  return 'Low Risk';
}

// Helper to build chart data based on threat intelligence
function getThreatChartData(result) {
  const intel = result.intelligence_results || {};
  let chartData = [
    { name: 'Safe', vendors: 0 },
    { name: 'Suspicious', vendors: 0 },
    { name: 'Malicious', vendors: 0 }
  ];

  // Malicious
  if (
    (intel.urlhaus && intel.urlhaus.is_malware) ||
    (intel.phishtank && intel.phishtank.is_phishing) ||
    (intel.google_safe_browsing && intel.google_safe_browsing.threats && intel.google_safe_browsing.threats.length > 0) ||
    (intel.ipqualityscore && (intel.ipqualityscore.is_malware || intel.ipqualityscore.is_phishing || intel.ipqualityscore.is_high_risk)) ||
    (intel.threatfox && intel.threatfox.is_malware)
  ) {
    chartData[2].vendors = 1;
  }
  // Suspicious
  else if (
    (intel.ipqualityscore && intel.ipqualityscore.is_suspicious) ||
    (intel.google_safe_browsing && intel.google_safe_browsing.threats && intel.google_safe_browsing.threats.some(t => t.threatType && t.threatType.toLowerCase().includes('suspicious')))
  ) {
    chartData[1].vendors = 1;
  }
  // Safe
  else {
    chartData[0].vendors = 1;
  }
  return chartData;
}

function App() {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);
    try {
      const response = await fetch('http://localhost:5001/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url }),
      });
      const data = await response.json();
      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || 'An error occurred');
      }
    } catch (err) {
      setError('Failed to connect to the server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h2" component="h1" align="center" gutterBottom sx={{ fontWeight: 700 }}>
          Cyber Threat Detector
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ color: 'text.secondary', mb: 4 }}>
          Real-time URL Threat Analysis
        </Typography>
        <Paper elevation={3} sx={{ p: 3, mb: 3, background: '#23272a', width: '100%' }}>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 2, mb: 2 }}>
            <TextField
              fullWidth
              label="Enter URL to analyze"
              variant="outlined"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="https://example.com"
              sx={{ input: { color: '#fff' } }}
            />
            <Button
              variant="contained"
              color="primary"
              type="submit"
              disabled={loading}
              sx={{ minWidth: 140, fontWeight: 600, fontSize: 18 }}
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : 'ANALYZE'}
            </Button>
          </Box>
          {error && (
            <Typography color="error" sx={{ mt: 1 }}>{error}</Typography>
          )}
        </Paper>
        {result && (
          <>
            <Paper elevation={3} sx={{ p: 4, background: '#23272a', width: '100%', mb: 4 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 2 }}>
                Analysis Results
              </Typography>
              <Grid container spacing={4}>
                {/* Left: Textual summary and stats */}
                <Grid item xs={12} md={6}>
                  <Typography variant="subtitle1" sx={{ color: getRiskColor(getRiskLevel(result)), fontWeight: 700, mb: 1 }}>
                    Risk Level: {getRiskLevel(result)}
                  </Typography>
                  <Typography variant="body2" sx={{ color: getRiskColor(getRiskLevel(result)), mb: 2 }}>
                    {result.vendor_message}
                  </Typography>
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      <b>Traffic Statistics</b>
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Daily Visitors: {result.traffic_stats?.daily_visitors?.toLocaleString()}
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Bounce Rate: {result.traffic_stats?.bounce_rate}%
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Average Visit Duration: {result.traffic_stats?.avg_visit_duration} minutes
                    </Typography>
                  </Box>
                  <Box sx={{ background: '#181a1b', p: 2, borderRadius: 2, mb: 2 }}>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      <b>Real-time Monitoring</b>
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Current Visitors: {result.real_time_monitoring?.current_visitors?.toLocaleString()}
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Peak Visitors: {result.real_time_monitoring?.peak_visitors?.toLocaleString()}
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Total Visits: {result.real_time_monitoring?.total_visits?.toLocaleString()}
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Trend: <span style={{ color: result.real_time_monitoring?.trend?.startsWith('+') ? '#4caf50' : '#ff5252' }}>{result.real_time_monitoring?.trend}</span>
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                      Last Updated: {result.real_time_monitoring?.last_updated}
                    </Typography>
                    <Typography variant="body2" sx={{ color: result.real_time_monitoring?.status === 'ACTIVE' ? '#4caf50' : '#ffb300', fontWeight: 600 }}>
                      {result.real_time_monitoring?.status === 'ACTIVE' ? 'âœ” Real-time monitoring active' : 'Real-time monitoring inactive'}
                    </Typography>
                  </Box>
                </Grid>
                {/* Right: Chart */}
                <Grid item xs={12} md={6}>
                  <Typography variant="subtitle1" sx={{ fontWeight: 700, mb: 1 }}>
                    Threat Analysis (Area Chart)
                  </Typography>
                  <Box sx={{ display: 'flex', justifyContent: 'center' }}>
                    <Box sx={{ height: 260, maxWidth: 450, width: '100%', background: '#181a1b', borderRadius: 2, p: 2 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <AreaChart data={getThreatChartData(result)}>
                          <defs>
                            <linearGradient id="colorRisk" x1="0" y1="0" x2="0" y2="1">
                              <stop offset="5%" stopColor="#00bcd4" stopOpacity={0.8}/>
                              <stop offset="95%" stopColor="#00bcd4" stopOpacity={0.1}/>
                            </linearGradient>
                          </defs>
                          <CartesianGrid stroke="#333" />
                          <XAxis dataKey="name" stroke="#fff" />
                          <YAxis stroke="#fff" allowDecimals={false} />
                          <Tooltip />
                          <Area
                            type="monotone"
                            dataKey="vendors"
                            stroke="#00bcd4"
                            fillOpacity={1}
                            fill="url(#colorRisk)"
                            isAnimationActive={true}
                            animationBegin={0}
                            animationDuration={1500}
                            animationEasing="ease"
                            dot={{ r: 6, fill: "#00bcd4" }}
                            activeDot={{ r: 10, stroke: "#fff", strokeWidth: 2, fill: "#00bcd4" }}
                          />
                        </AreaChart>
                      </ResponsiveContainer>
                    </Box>
                  </Box>
                  <Typography variant="caption" sx={{ color: 'text.secondary', mt: 1, display: 'block' }}>
                    The chart shows the number of security vendors that have classified this URL as safe, suspicious, or malicious. Hover over the data points for detailed information.
                  </Typography>
                </Grid>
              </Grid>
            </Paper>

            {/* Security Analysis Section (previously designed) */}
            <Paper elevation={3} sx={{ p: 4, background: '#23272a', width: '100%' }}>
              {/* Risk Level Banner */}
              <Box sx={{
                background: getRiskLevel(result) === 'Low Risk' ? '#7ee787' : getRiskLevel(result) === 'Medium Risk' ? '#ffe066' : '#ff7b72',
                color: '#222',
                borderRadius: 2,
                p: 3,
                mb: 3,
                textAlign: 'left',
              }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  Risk Level: {getRiskLevel(result)}
                </Typography>
                <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                  Overall Risk Score: {Math.round((result.threat_score || 0) / 10)}/10
                </Typography>
              </Box>

              {/* Security Summary */}
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                  Security Summary
                </Typography>
                <Box sx={{ ml: 1 }}>
                  <Typography variant="body2" sx={{ color: '#4caf50', mb: 0.5 }}>
                    âœ“ The website uses a valid SSL certificate, ensuring secure data transmission.
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#4caf50', mb: 0.5 }}>
                    âœ“ The domain has 4 types of DNS records configured, indicating proper setup.
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#ffb300', mb: 0.5 }}>
                    âš  Warning: The website is missing important security headers: content_security_policy, strict_transport_security, x_content_type_options
                  </Typography>
                </Box>
              </Box>

              {/* Info Cards Row */}
              <Grid container spacing={2} sx={{ mb: 3 }}>
                {/* SSL Certificate */}
                <Grid item xs={12} md={4}>
                  <Box sx={{ background: '#181a1b', borderRadius: 2, p: 2, height: '100%' }}>
                    <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1 }}>
                      SSL Certificate
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#4caf50' }}>
                      âœ“ Valid SSL Certificate
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#b0b3b8' }}>
                      Issuer: Google Trust Services
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#b0b3b8' }}>
                      Valid Until: 7/14/2025
                    </Typography>
                  </Box>
                </Grid>
                {/* Domain Information */}
                <Grid item xs={12} md={4}>
                  <Box sx={{ background: '#181a1b', borderRadius: 2, p: 2, height: '100%' }}>
                    <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1 }}>
                      Domain Information
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#ff5252' }}>
                      Domain information unavailable
                    </Typography>
                  </Box>
                </Grid>
                {/* IP Reputation */}
                <Grid item xs={12} md={4}>
                  <Box sx={{ background: '#181a1b', borderRadius: 2, p: 2, height: '100%' }}>
                    <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1 }}>
                      IP Reputation
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#fff' }}>
                      IP Address: 142.251.222.164
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#4caf50' }}>
                      âœ“ Public IP
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#fff' }}>
                      DNS Records: 4 types found
                    </Typography>
                  </Box>
                </Grid>
              </Grid>

              {/* URL Pattern Analysis */}
              <Box sx={{ mb: 3 }}>
                <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1 }}>
                  URL Pattern Analysis
                </Typography>
                <Typography variant="body2" sx={{ color: '#fff' }}>
                  Pattern Risk Score: 0
                </Typography>
              </Box>

              {/* Technology Stack */}
              <Box>
                <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1 }}>
                  Technology Stack
                </Typography>
                <Typography variant="body2" sx={{ color: '#fff' }}>
                  Server: gws
                </Typography>
                <Typography variant="body2" sx={{ color: '#fff' }}>
                  Powered By: Unknown
                </Typography>
                <Typography variant="body2" sx={{ color: '#fff', mt: 1 }}>
                  Security Headers:
                </Typography>
                <Box sx={{ ml: 2 }}>
                  <Typography variant="body2" sx={{ color: '#ffb300' }}>
