import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Paper,
  Box,
  CircularProgress,
  Alert,
  Grid,
  Card,
  CardContent,
  CardHeader,
  Divider,
  CssBaseline,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import {
  Security,
  Warning,
  CheckCircle,
  Error,
  Speed,
  Link,
} from '@mui/icons-material';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#181a1b',
      paper: '#23272a',
    },
    primary: {
      main: '#2196f3',
    },
    secondary: {
      main: '#f50057',
    },
    text: {
      primary: '#fff',
      secondary: '#b0b3b8',
    },
  },
  typography: {
    fontFamily: 'Inter, Roboto, Arial, sans-serif',
  },
});

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:5001',
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true,
  timeout: 30000 // Increased timeout to 30 seconds
});

function App() {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const analyzeUrl = async () => {
    if (!url) {
      setError('Please enter a URL');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      console.log('Sending request to analyze URL:', url);
      const response = await api.post('/api/analyze', { url });
      console.log('Received response:', response.data);
      setResult(response.data);
    } catch (err) {
      console.error('Error details:', err);
      if (err.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        setError(err.response.data.error || 'Server error occurred');
      } else if (err.request) {
        // The request was made but no response was received
        setError('No response from server. Please check if the backend is running.');
      } else {
        // Something happened in setting up the request that triggered an Error
        setError('Error setting up the request');
      }
    } finally {
      setLoading(false);
    }
  };

  const getRiskColor = (level) => {
    switch (level) {
      case 'HIGH':
        return '#ff5252';
      case 'MEDIUM':
        return '#ffb300';
      case 'LOW':
        return '#4caf50';
      default:
        return '#2196f3';
    }
  };

  const chartData = [
    { name: 'Safe', vendors: 72 },
    { name: 'Suspicious', vendors: 5 },
    { name: 'Malicious', vendors: 1 },
  ];

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Container maxWidth="md" sx={{ py: 4 }}>
        <Typography variant="h2" component="h1" align="center" gutterBottom sx={{ fontWeight: 700 }}>
          Cyber Threat Detector
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ color: 'text.secondary', mb: 4 }}>
          Real-time URL Threat Analysis
        </Typography>
        <Paper elevation={3} sx={{ p: 3, mb: 3, background: '#23272a' }}>
          <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
            <TextField
              fullWidth
              label="Enter URL to analyze"
              variant="outlined"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="https://example.com"
              sx={{ input: { color: '#fff' } }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={analyzeUrl}
              disabled={loading}
              sx={{ minWidth: 140, fontWeight: 600, fontSize: 18 }}
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : 'ANALYZE'}
            </Button>
          </Box>

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          {result && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 2 }}>
                Analysis Results
              </Typography>
              <Paper elevation={2} sx={{ p: 3, background: '#181a1b' }}>
                <Typography variant="subtitle1" sx={{ color: getRiskColor(result.threat_level), fontWeight: 700, mb: 1 }}>
                  Risk Level: {result.threat_level === 'LOW' ? 'Low Risk' : result.threat_level === 'MEDIUM' ? 'Medium Risk' : 'High Risk'}
                </Typography>
                {result.threat_reasons && result.threat_reasons.length > 0 && (
                  <Typography variant="body2" sx={{ color: '#ffb300', mb: 2 }}>
                    {result.threat_reasons.join(', ')}
                  </Typography>
                )}
                <Grid container spacing={4}>
                  {/* Left Column: Stats */}
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Traffic Statistics
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Domain:</b> {result.domain}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>SSL Security:</b> {result.ssl_secure ? 'Secure' : 'Not Secure'}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>DNS Resolution:</b> {result.dns_resolution ? 'Resolved' : 'Failed'}
                      </Typography>
                      {result.domain_age_days !== null && (
                        <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                          <b>Domain Age:</b> {result.domain_age_days} days
                        </Typography>
                      )}
                      {result.open_ports && result.open_ports.length > 0 && (
                        <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                          <b>Open Ports:</b> {result.open_ports.join(', ')}
                        </Typography>
                      )}
                    </Box>
                    <Divider sx={{ my: 2, background: '#333' }} />
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Real-time Monitoring
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Suspicious Patterns:</b> {result.suspicious_patterns && result.suspicious_patterns.length > 0 ? result.suspicious_patterns.join(', ') : 'None'}
                      </Typography>
                      {result.security_headers && (
                        <Box sx={{ mt: 1 }}>
                          <Typography variant="body2" sx={{ color: 'text.secondary', mb: 1 }}>
                            <b>Security Headers:</b>
                          </Typography>
                          {Object.entries(result.security_headers).map(([header, value]) => (
                            <Typography key={header} variant="caption" sx={{ color: value === 'Not Set' ? '#ffb300' : '#4caf50', ml: 2 }}>
                              {header}: {value}
                            </Typography>
                          ))}
                        </Box>
                      )}
                    </Box>
                  </Grid>
                  {/* Right Column: Threat Analysis & Chart */}
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Threat Analysis
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Threat Level:</b> {result.threat_level}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        <b>Reasons:</b> {result.threat_reasons && result.threat_reasons.length > 0 ? result.threat_reasons.join(', ') : 'None'}
                      </Typography>
                    </Box>
                    <Divider sx={{ my: 2, background: '#333' }} />
                    <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                      Security Vendor Analysis
                    </Typography>
                    <Box sx={{ height: 220, background: '#23272a', borderRadius: 2, p: 2 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={chartData}>
                          <CartesianGrid stroke="#333" />
                          <XAxis dataKey="name" stroke="#fff" />
                          <YAxis stroke="#fff" />
                          <Tooltip />
                          <Line type="monotone" dataKey="vendors" stroke="#2196f3" strokeWidth={3} dot={{ r: 6 }} />
                        </LineChart>
                      </ResponsiveContainer>
                    </Box>
                  </Grid>
                </Grid>
                <Divider sx={{ my: 3, background: '#333' }} />
                <Typography variant="h6" sx={{ mb: 1, color: 'text.primary' }}>
                  Recommendations
                </Typography>
                <Grid container spacing={1}>
                  {result.recommendations.map((rec, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                      <Typography variant="body2" sx={{ color: '#4caf50', display: 'flex', alignItems: 'center' }}>
                        <CheckCircle color="success" sx={{ mr: 1, fontSize: 16 }} />
                        {rec}
                      </Typography>
                    </Grid>
                  ))}
                </Grid>
              </Paper>
            </Box>
          )}
        </Paper>
      </Container>
    </ThemeProvider>
  );
}

export default App;
